example.programlisting:
foreach fit [fit list] {
  set name [lindex $fit 0]
  eval     [fit proc $name]
  rename fitline $name
}

for {set i 0 } { $i < 128 } {incr i } {
   for {set j 0} {$j < 128 } {incr j } {}
      channel -set TwodSpectrum [list $i $j] 0]
    }
}

