  typedef struct  _Pixel {
    int    x, y;
    int    z;                            
  } Pixel, *pPixel;

  
  
public:
  Projection();
  ~Projection();                        

  // Setting the projection data:
  
  void clear();                         
  void setDimension(int x, int y);      
  void setPixel(int x, int y, int z);   

  // Iterating nonzero pixels of the projection.
  
  pPixel begin();                       
  pPixel end();                         
  pPixel next(pPixel p);                
};

