#ifndef FILLIMAGE_H
#define FILLIMAGE_H

#include <EventProcessor.h>
#include <string>
#include <TreeParameter.h>

class Projection;

class FillImage : public CEventProcessor
{
private:
  Projection&    m_rProjection;
  std::string        m_spectrumName;
  CTreeVariable      m_updateFlag;

public:
  FillImage(Projection& p, const char* spName, const char* flagName);

  Bool_t operator()(const Address_t pEvent, CEvent& rEvent,
                    CAnalyzer& rAnalyzer, CBufferDecoder& rDecoder
                    );



};

#endif

#include "FillImage.h"
#include "projection.h"


FillImage:: FillImage(Projection& p, const char* spName, const char* flagName) :
  m_rProjection(p),
  m_spectrumName(spName),
  m_updateFlag(flagName, 0.0, "Flag")
{}

Bool_t
FillImage::operator()(const Address_t pEvent, CEvent& rEvent,
                      CAnalyzer& rAna, CBufferDecoder& rDecoder)
{
  if (m_updateFlag) {
    m_updateFlag = 0.0;                           
    SpecTcl*   pApi  = SpecTcl::getInstance();
    CSpectrum* pSpec = pApi->FindSpectrum(m_spectrumName);  

    if (pSpec) {
      CSpectrum2DL* p2d = dynamic_cast<CSpectrum2DL*>(pSpec); 
      if (p2d) {                                   
        p2d->Clear();                           
        Size_t xdim = p2d->Dimension(0);      
        Size_t ydim = p2d->Dimension(1);        
        UInt_t indices[2];


        Projection::pPixel p;
        for (p = m_rProjection.begin(); p != m_rProjection.end(); p = m_rProjection.next(p)) {
          if ((p->x < xdim) && (p->y < ydim)) {  
            indices[0] = p->x;
            indices[1] = p->y;
            p2d->set(indices, p->z);            
          }
        }

      }
    }
  }
  return kfTRUE;                                  
}

#ifndef GATEPROCESSOR_H
#define GATEPROCESSOR_H

#include <EventProcessor.h>
#include <TreeParameter.h>
#include <vector>

class CGateContainer;


class GateProcessor : public CEventProcessor
{
private:
  CTreeParameter m_pid;
  std::vector<CGateContainer*> m_gates;
public:
  GateProcessor();
  Bool_t OnInitialize();

  Bool_t operator()(const Address_t pEvent, CEvent& rEvent,
		    CAnalyzer& rAna, CBufferDecoder& rDec);
};

#endif

#include "GateProcessor.h"
#include <SpecTcl.h>
#include <GateContainer.h>
#include <Gate.h>


GateProcessor::GateProcessor() :
  m_pid("PID")
{}

static const char* gateNames[] = {
  "Protons", "Deuterons", "Tritons", "3He", "Alphas", 0
};                                                
static float lows[] = {0, 100, 200, 300, 400};
static float highs[]  = {50, 150, 250, 350, 450};

static const char* pGateParam="raw.00";           

Bool_t
GateProcessor::OnInitialize()
{
  SpecTcl& api(*SpecTcl::getInstance());

  const char** pNames(gateNames);
  float* pLows(lows);
  float* pHighs(highs);

  while (*pNames) {
    CGate* pCut = api.CreateCut(pGateParam, *pLows, *pHighs); 
    api.AddGate(*pNames, pCut);                               

    CGateContainer* pContainer = api.FindGate(*pNames);       
    assert(pContainer);                   // Must have a matching container.

    m_gates.push_back(pContainer);                           
    
    pNames++;
    pLows++;
    pHighs++;
  }
  return kfTRUE;
}

Bool_t
GateProcessor::operator()(const Address_t pEvent, CEvent& rEvent,
			  CAnalyzer& rAnA, CBufferDecoder& rDecoder)
{
  for (int i = 0; i < m_gates.size(); i++) {
    CGateContainer& p(*(m_gates[i]));            
    p->Reset();                                   
    if ((*p)(rEvent)) {                              
      m_pid = i;                                     
      break;
    }
  }
  return kfTRUE;
}

#ifndef EVPSWITCHER_H
#define EVPSWITCHER_H
